import React from 'react';

import { ListItem } from './ListItem';

export class ItemList extends React.Component {
  state = { items: [], statuses: [] }

  async componentDidMount() {
    await this.fetchItems();
  }

  async fetchItems() {
    const statuses = await fetch('http://localhost:3000/itemStatuses');
    this.setState({ statuses: await statuses.json() });
  }

  async onSelectItem(selected) {
    this.props.onSelectItem(selected);

    await fetch(`http://localhost:3000/itemStatuses/${selected.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'text/plain' },
      body: 'seen',
    });

    await this.fetchItems();
  }

  render() {
    let items;

    if (this.props.amount <= this.props.items.length) {
      items = this.props.items.slice(0, this.props.amount);
    } else {
      items = this.props.items;
    }

    return items.map(item => (
      <ListItem
        item={item}
        selected={this.props.selected?.id === item.id}
        status={this.state.statuses[item.id] ?? null}
        key={item.id}
        onSelectItem={this.onSelectItem}
      />
    ));
  }
}
