import React from 'react';

export class PreferenceDialog extends React.Component {

  state = { amount: this.props.itemAmount, color: this.props.color };

  onSave = () => {
    this.props.onSave(this.props.color, this.props.itemAmount);
  }

  render() {
    return (
      <div id="ContentPanel" className="preferences">
        <div className="PreferencesDialog">
          <header>
            <div className="Logo">
              <div className="colored">RrHN</div>
              <div className="title">Settings</div>
            </div>
          </header>
          <label htmlFor="listSizeField">
            Show <input type="number" id="listSizeField" value={this.props.itemAmount} /> items in the list.
          </label>
          <label htmlFor="colorField">
            color:
            <select value={this.props.value} id="colorField">
              <option>orange</option>
              <option>green</option>
              <option>brown</option>
            </select>
          </label>
          <div className="dialogButtons">
            <button onClick={this.onSave}>OK</button>
            <button onClick={() => this.props.onCancel()}>Cancel</button>
          </div>
        </div>
      </div>
    );
  }
}
