import React from 'react';

import { ListItem } from './ListItem';

const ItemList = ({ items, amount, statuses, onSelectItem }) => {
  let available;

  if (this.props.amount <= this.props.items.length) {
    available = this.props.items.slice(0, this.props.amount);
  } else {
    available = this.props.items;
  }

  return available.map(item => (
    <ListItem
      item={item}
      status={this.props.statuses[item.id] ?? null}
      key={item.id}
      onSelectItem={this.props.onSelectItem}
    />
  ));
}

export default ItemList;
